(()=>{"use strict";const allMenuItems=document.querySelectorAll(".menu-item"),navMenu=document.querySelector(".nav-main"),menuText=document.querySelector(".menu-button-text"),menuButtonWrap=document.querySelector(".menu-toggle");allMenuItems.forEach((menuItem=>{"Close"==menuItem.innerText&&menuItem.classList.add("nav-main-close")}));document.querySelector(".nav-main-close").onclick=()=>{navMenu.classList.toggle("nav-main-open"),menuButtonWrap.classList.toggle("opened"),menuText.classList.toggle("menu-button-text-red"),"Menu"===menuText.innerHTML?menuText.innerHTML="Close":menuText.innerHTML="Menu"}})();