(()=>{"use strict";const buttonSection=document.querySelector(".dinners-buttons"),eventButtons=document.querySelectorAll(".dinners-button"),menuDivs=document.querySelectorAll(".dinners-menu");buttonSection.addEventListener("click",(function(event){let activeButton=event.target,activeButtonData=event.target.dataset.button;eventButtons.forEach((eventButton=>{eventButton.classList.contains("dinners-button-active")&&eventButton.classList.remove("dinners-button-active")})),activeButton.classList.add("dinners-button-active"),menuDivs.forEach((menu=>{menu.dataset.flavor==activeButtonData?menu.classList.add("dinners-menu-active"):menu.classList.remove("dinners-menu-active")}))}))})();