(()=>{"use strict";const homeShareButton=document.querySelector(".home-share"),pageURL=document.querySelector("link[rel=canonical]"),pageTitle=document.title,contentElemPos=document.querySelector(".content").offsetTop;navigator.share&&(window.addEventListener("scroll",(function(){let scrollPos=window.scrollY;homeShareButton.style.display=scrollPos>=contentElemPos?"block":"none"})),homeShareButton.addEventListener("click",(()=>navigator.share({title:pageTitle,url:pageURL.href}))))})();